export default (routers, data) => {
  getRouterObj(routers, data)
}
function getRouterObj (route, data) {
  data.children.forEach((item) => {
    if (item.children.length > 0) {
      getRouterObj(route, item)
    } else {
      /* TODO 判断是否存在此路由 */
      let tmp = {}
      tmp.path = '/' + item.resCode
      tmp.name = item.resCode
      if (item.resUrl !== '') {
        tmp.component = (resolve) => require([`../views/components/${item.resUrl}.vue`], resolve)
        // tmp.component = (resolve) => require(['../views/components/Demo/CRUD.vue'], resolve)
      } else {
        tmp.component = (resolve) => require(['../views/Dash.vue'], resolve)
      }
      route.push(tmp)
    }
  })
}
