          }
        ],
        tableType: '3',
        dataSource: [], // 当前页的数据
        /** filter **/
        templTableData: [], // 临时记录tableDate的值
        /** 弹出层 **/
        edit: {
          title: '参数编辑',
          type: '2',
          dialogVisible: false,
          data: {
            form: {
              rowId: '',
              paramId: '',
              entityId: '',
